kickstart.post:
#!/bin/sh # # chkconfig: 35 99 01 # # description: Start the service that tracks downloaded packages # . /etc/rc.d/init.d/functions
start() { echo -n "Starting Rocks Tracker: " daemon '/opt/rocks/bin/tracker-server > /var/log/tracker-server.log \ 2>&1 &' RETVAL=$?
touch /var/lock/subsys/tracker-server return $RETVAL }
stop() { action "Stopping Rocks Tracker: " killall tracker-server RETVAL=$?
rm -f /var/lock/subsys/tracker-server return $RETVAL }
# # main # case "$1" in start) start ;;
stop) stop ;;
restart) stop start ;;
*) echo "Usage: $0 {start|stop|restart}" RETVAL=1 esac
exit $RETVAL
/sbin/chkconfig rocks-tracker on
